import mongoose from 'mongoose';

const appSchema = new mongoose.Schema(
  {
    appName: {
      type: String,
      required: true,
      trim: true
    },
    websiteUrl: {
      type: String,
      required: true,
      trim: true
    },
    email: {
      type: String,
      required: true,
      trim: true
    },
    paymentId: {
      type: String,
      default: null
    },
    downloadLink: {
      type: String,
      default: null
    },
    status: {
      type: String,
      enum: ['pending', 'paid', 'completed'],
      default: 'pending'
    },
    createdAt: {
      type: Date,
      default: Date.now
    }
  },
  { collection: 'apps' }
);

export default mongoose.model('App', appSchema);
